import React from "react";
import { useState } from 'react';
import { Link } from 'react-router-dom';
import { makeStyles } from "@material-ui/core/styles";
import { FaLinkedin } from 'react-icons/fa';
import flower from './fwr.svg';
import { TypeAnimation } from 'react-type-animation';
import { AppBar, Toolbar, Typography, Button, Grid, Paper } from "@material-ui/core";


const useStyles = makeStyles((theme) => ({
  root: {
    flexGrow: 1,
  },
  appBar: {
    backgroundColor: theme.palette.background.paper,
    borderBottom: "1px solid pink", 
    boxShadow: "none",
    fontFamily: 'Palanquin',
    fontSize: 150,
  },
  links:{
    fontSize: 10,
  },
  title: {
    flexGrow: 1,
    paddingLeft: '50%',
    fontWeight: "bold",
    color: "black",
    fontFamily: 'Palanquin',

  },
  summarySection: {
    marginTop: 60,
    marginLeft: 50,
    padding: theme.spacing(3),
    fontFamily: 'Gallient',
    fontSize: 150,
    fontWeight: 20,
    animation: "type 4s steps(60, end)",

  },
  top: {
    fontFamily: 'Gallient',
    marginLeft: 50,
    fontSize: 20,
    fontWeight: 20,
    animation: "type 4s steps(60, end)",

  },
  comment: {
    marginTop: -550,
    marginLeft: 700,
    padding: theme.spacing(3),
    fontFamily: 'Gallient',
    fontSize: 30,
    fontWeight: 20,

  },
  interactiveArt: {
    marginTop: -70,
    width: 100,
    height: '100%',
  },
  linkedinButton: {
    marginTop: -3,

  },
}));

const Homepage = () => {
  const classes = useStyles();

  const [linkedinUrl] = useState('https://www.linkedin.com/in/micatonge');

  const handleLinkedinClick = () => {
    window.open(linkedinUrl, '_blank');
  };

  window.onload = function() {
    window.setTimeout(fadeout, 8000); //8 seconds
  }

  function fadeout() {
    document.getElementById('fadeout').style.opacity = '0';
  }

  return (
    <div className={classes.root}>
      <AppBar position="static" className={classes.appBar}>
        <Toolbar className={classes.appBar} >
          <Button className={classes.links} component={Link} to="/">Home</Button>
          <Button className={classes.links} component={Link} to="/about">About Me</Button>
          <Button className={classes.links} component={Link} to="/projects">Projects</Button>
          <Button className={classes.links} component={Link} to="/resume">Resume</Button>
          <Button className={classes.links} component={Link} to="/contact">Contact Me</Button>
          <Button className={classes.linkedinButton} onClick={handleLinkedinClick}>
            <FaLinkedin size={20} />
          </Button>
          {/* <Typography variant="h6" className={classes.title}>
            Mica Tonge
          </Typography> */}
        </Toolbar>
      </AppBar>
      <Grid container>
        <Grid item xs={12} md={6}>
            <Typography className={classes.summarySection} variant="h6">Mica <br></br> Tonge</Typography>
            <Typography className={classes.top} variant="h6">based in new york. open to relocation âœº</Typography>

            <Typography id="fadeout" className={classes.comment} variant="h1"> you can find me coding . . .</Typography>

        </Grid>
        {/* <Grid item xs={2} md={3} className={classes.interactiveArt}>
        <img src={flower} alt="logo" />
        </Grid> */}
      </Grid>
    </div>
  );
};

export default Homepage;
